import { ServerValues } from "../../utility/ServerValues";

/**
 * 
 * @param {String} str 
 * @param {String} ending 
 */
export function removeEnding(str, ending) {
    if (str === undefined || str === null || str.length === 0 ||
        ending === undefined || ending === null || ending.length === 0) {
        return str;
    }
    if (str.endsWith(ending)) {
        var i = str.lastIndexOf(ending);
        return str.substring(0, i);
    }
    return str;
}
export function getCountries() {
    const sv = new ServerValues().getValues();
    const culture = sv['culture'];
    const fallbackCulture = sv['fallback-culture'];
    const countries = window.wCountries;
    return countries[culture] || countries[fallbackCulture];
}