class BrowserFileApiSupport {
    supportsFileInput() {
        if (
            navigator.userAgent.match(
                /(Android (1.0|1.1|1.5|1.6|2.0|2.1))|(Windows Phone (OS 7|8.0))|(XBLWP)|(ZuneWP)|(w(eb)?OSBrowser)|(webOS)|(Kindle\/(1.0|2.0|2.5|3.0))/
            )
        ) {
            return false;
        }
        const el = document.createElement("input");
        el.type = "file";
        return !el.disabled;
    }
    
    supportsFileApi() {
        return window.FileReader && !!window.CanvasRenderingContext2D;
    }
}

const browserFileApiSupport = new BrowserFileApiSupport();
export {browserFileApiSupport}
