<template>
    <div style="width: 100%;max-height: 700px;height:500px;direction:ltr;text-align:left;">
        <file-manager></file-manager>
    </div>
</template>

<script>
import FileManager from './FileManager.vue';
export default {
    components: {
        FileManager
    },
    created() {
        $(document).off('fm_file_select', this.onSelection);
        $(document).on('fm_file_select', this.onSelection);
    },
    methods: {
        onSelection(ev, items) {
            this.$emit('selected', items);
        }
    }
}
</script>