<template>
    <b-modal ref="modal" hide-footer :title="title" size="lg" @shown="shown" @hidden="hidden">
        <component :is="compName" @selected="onSelected"></component>
    </b-modal>
</template>

<script>
export default {
    props: {
        value: {
            default: false,
        },
        title: {
            default: ''
        },
    },
    data() {
        return {
            visible: false,
            compName: 'div'
        };
    },
    mounted() {
        this.$nextTick(() => {
            this.valueChanged();
        });
    },
    methods: {
        onSelected(items) {
            this.$emit('selected', items);
        },
        shown() {
            this.visible = true;
            this.compName = 'w-filer';
        },
        hidden() {
            this.visible = false;
            this.compName = 'div';
        },
        valueChanged() {
            this.visible = this.value;
            if (this.visible) {
                this.$refs.modal.show();
            } else {
                this.$refs.modal.hide();
            }
        }
    },
    watch: {
        value() {
            if (this.visible != this.value) {
                this.valueChanged();
            }
        },
        visible() {
            if (this.visible != this.value) {
                this.$emit('input', this.visible);
            }
        }
    }
}
</script>