<template>
    <div class="fm-tree">
        <div class="fm-tree-disk sticky-top">
            <i class="far fa-hdd"></i>
            {{ selectedDisk }}
        </div>
        <branch v-bind:parent-id="0"></branch>
    </div>
</template>

<script>
import Branch from './Branch.vue';

export default {
    name: 'FolderTree',
    components: {
        branch: Branch,
    },
    computed: {
        /**
         * Selected Disk
         * @returns {*}
         */
        selectedDisk() {
            return this.$store.getters['fm/selectedDisk'];
        },
    },
};
</script>