<template>
    <div class="row">
        <div class="col-12">
            <div class="input-group">
                <div class="input-group-prepend">
                    <span class="input-group-text" :style="labelStyle">{{$t('width')}}</span>
                </div>
                <input placeholder="150px or 100%" class="form-control" v-model="config.width" dir="ltr" />
                <div class="input-group-append">
                    <button
                        class="btn btn-primary"
                        :title="$t('full-width')"
                        :style="buttonStyle"
                        @click="fullWidthClick"
                    >
                        <svg style="width:24px;height:24px" viewBox="0 0 24 24">
                            <path fill="#ffffff" d="M8,14V18L2,12L8,6V10H16V6L22,12L16,18V14H8Z" />
                        </svg>
                    </button>
                </div>
            </div>
        </div>
        <div class="col-12">
            <div class="input-group">
                <div class="input-group-prepend">
                    <span class="input-group-text" :style="labelStyle">{{$t('min-width')}}</span>
                </div>
                <input placeholder="150px or 100%" class="form-control" v-model="config.minWidth" dir="ltr" />
            </div>
        </div>
        <div class="col-12">
            <div class="input-group">
                <div class="input-group-prepend">
                    <span class="input-group-text" :style="labelStyle">{{$t('max-width')}}</span>
                </div>
                <input placeholder="150px or 100%" class="form-control" v-model="config.maxWidth" dir="ltr" />
            </div>
        </div>
        <div class="col-12">
            <div class="input-group">
                <div class="input-group-prepend">
                    <span class="input-group-text" :style="labelStyle">{{$t('height')}}</span>
                </div>
                <input placeholder="150px or 100%" class="form-control" v-model="config.height" dir="ltr" />
            </div>
        </div>
        <div class="col-12">
            <div class="input-group">
                <div class="input-group-prepend">
                    <span class="input-group-text" :style="labelStyle">{{$t('min-height')}}</span>
                </div>
                <input placeholder="150px or 100%" class="form-control" v-model="config.minHeight" dir="ltr" />
            </div>
        </div>
        <div class="col-12">
            <div class="input-group">
                <div class="input-group-prepend">
                    <span class="input-group-text" :style="labelStyle">{{$t('max-height')}}</span>
                </div>
                <input placeholder="150px or 100%" class="form-control" v-model="config.maxHeight" dir="ltr" />
            </div>
        </div>
    </div>
</template>

<script>
import loc from './image-url.i18n.json';
import ImageUrl from './ImageUrl';
import { i18n } from "../../../../utility/VueI18n";
const d = new ImageUrl();
export default {
    loc: i18n(loc),
    props: {
        value: {
            type: Object,
            default() {
                return d.getEmptySettingsInData();
            }
        }
    },
    data() {
        return {
            config: Object.assign({}, this.value)
        };
    },
    computed: {
        buttonStyle() {
            return {
                width: '50px'
            };
        },
        labelStyle() {
            return {
                width: '150px'
            };
        },
    },
    methods: {
        fullWidthClick() {
            this.config.width = '100%';
        }
    },
    watch: {
        value: {
            handler() {
                if (!_.isEqual(this.config, this.value)) {
                    this.config = Object.assign({}, this.config, this.value);
                }
            },
            deep: true
        },
        config: {
            handler() {
                if (!_.isEqual(this.config, this.value)) {
                    this.$emit('input', this.config);
                }
            },
            deep: true
        },
    }
}
</script>