<template>
    <vue-trix v-model="html"></vue-trix>
    <!-- <w-medium-editor v-model="html" :buttons="buttons"></w-medium-editor> -->
</template>

<script>
import loc from './paragraph.i18n.json';
import Paragraph from './Paragraph';
import { i18n } from "../../../../utility/VueI18n";
const d = new Paragraph();
export default {
    i18n: i18n(loc),
    props: {
        /**
         * object of type {
         *  text: ''
         * }
         */
        value: {
            type: Object,
            default() {
                return d.getEmptyData();
            }
        },
        config: {
            type: Object,
            default() {
                return d.getEmptyConfig();
            }
        },
    },
    data() {
        return {
            html: this.value.text,
            buttons: [
                'bold',
                'italic',
                'underline',
                'strikethrough',
                'subscript',
                'superscript',
                'anchor',
                'quote',
                'pre',
                'orderedlist',
                'unorderedlist',
                'removeFormat',
                'highlight',
                'h1',
                'h2',
                'h3',
            ],
        };
    },
    watch: {
        'value.text': {
            handler() {
                if (this.value.text !== this.html) {
                    this.html = this.value.text;
                }
            },
            deep: true
        },
        html() {
            if (this.value.text !== this.html) {
                this.$emit('input', {
                    text: this.html
                });
            }
        },
    }
}
</script>