import Block from '../Block';
export default class Text extends Block {
    getType() {
        return 'text';
    }
    getEmptyData() {
        return {
            text: ''
        };
    }
    isDataValid(data) {
        var keys = Object.keys(data);
        return keys.length === 1 && keys[0] === "text" && !_.isNil(data.text);
    }
    getEmptyConfig() {
        return {};
    }
    mergeData(dest, src) {
        dest.text = src.text;
    }
}