<template>
    <w-image-select :max-count="1" v-model="array" />
</template>

<script>
export default {
    props: {
        value: {
            type: String,
            default: ''
        },
    },
    data() {
        return {
            array: []
        };
    },
    mounted() {
        this.valueChanged();
    },
    methods: {
        getOne() {
            if (_.isEmpty(this.array)) {
                return '';
            }
            return this.array[0];
        },
        valueChanged() {
            var v = this.getOne();
            if (v !== this.value && !_.isEmpty(this.value)) {
                this.array.splice(0, this.array.length);
                this.array.push(this.value);
            }
        }
    },
    watch: {
        value() {
            this.valueChanged();
        },
        array: {
            handler() {
                var v = this.getOne();
                if (this.value !== v) {
                    this.$emit('input', v);
                }
            },
            deep: true
        }
    }
}
</script>