'use strict';
module.exports = function localize_fa(errors) {
  if (!(errors && errors.length)) return;
  for (var i = 0; i < errors.length; i++) {
    var e = errors[i];
    var out;
    switch (e.keyword) {
      case '$ref':
        out = 'رفرنس ' + (e.params.ref) + ' قابل شناسایی نیست';
        break;
      case 'additionalItems':
        out = '';
        var n = e.params.limit;
        out += 'نباید بیشتر از ' + (n) + ' آیتم داشته باشد';
        break;
      case 'additionalProperties':
        out = 'نباید مشخصه های اضافی داشته باشد';
        break;
      case 'anyOf':
        out = 'باید با مقادیری از اسکیمای "anyOf" تطابق داشته باشد';
        break;
      case 'const':
        out = 'باید مقدار ثابت باشد';
        break;
      case 'contains':
        out = 'باید یک آیتم معتبر داشته باشد';
        break;
      case 'custom':
        out = 'باید اعتبارسنجی مربوط به کلمه کلیدی "' + (e.keyword) + '" را با موفقیت پاس کند';
        break;
      case 'dependencies':
        out = '';
        var n = e.params.depsCount;
        out += 'باید دارای مشخصه هایی باشد ' + (e.params.deps) + ' زمانی که مشخصه ' + (e.params.property) + ' حاضر است';
        break;
      case 'enum':
        out = 'باید با یکی از مقادیر از قبل تعریف شده، برابر باشد';
        break;
      case 'exclusiveMaximum':
        out = '';
        var cond = e.params.comparison + " " + e.params.limit;
        out += 'بایستی ' + (cond) + ' باشد';
        break;
      case 'exclusiveMinimum':
        out = '';
        var cond = e.params.comparison + " " + e.params.limit;
        out += 'بایستی ' + (cond) + ' باشد';
        break;
      case 'false schema':
        out = 'اسکیما مقدار بولین خطا دارد';
        break;
      case 'format':
        out = 'باید با فرمت "' + (e.params.format) + '" تطابق داشته باشد';
        break;
      case 'formatExclusiveMaximum':
        out = 'formatExclusiveMaximum باید مقدار بولین باشد';
        break;
      case 'formatExclusiveMinimum':
        out = 'formatExclusiveMaximum باید مقدار بولین باشد';
        break;
      case 'formatMaximum':
        out = '';
        var cond = e.params.comparison + " " + e.params.limit;
        out += 'باید برابر باشد با ' + (cond);
        break;
      case 'formatMinimum':
        out = '';
        var cond = e.params.comparison + " " + e.params.limit;
        out += 'باید برابر باشد با ' + (cond);
        break;
      case 'if':
        out = 'باید با اسکیمای "' + (e.params.failingKeyword) + '" تطابق داشته باشد';
        break;
      case 'maximum':
        out = '';
        var cond = e.params.comparison + " " + e.params.limit;
        out += 'بایستی ' + (cond) + ' باشد';
        break;
      case 'maxItems':
        out = '';
        var n = e.params.limit;
        out += 'نباید بیشتر از ' + (n) + ' آیتم داشته باشد';
        break;
      case 'maxLength':
        out = '';
        var n = e.params.limit;
        out += 'نباید طولانی تر از ' + (n) + ' کاراکتر باشد';
        break;
      case 'maxProperties':
        out = '';
        var n = e.params.limit;
        out += 'نباید بیشتر از ' + (n) + ' مشخصه داشته باشد';
        break;
      case 'minimum':
        out = '';
        var cond = e.params.comparison + " " + e.params.limit;
        out += 'بایستی ' + (cond) + ' باشد';
        break;
      case 'minItems':
        out = '';
        var n = e.params.limit;
        out += 'نباید کمتر از ' + (n) + ' آیتم داشته باشد';
        break;
      case 'minLength':
        out = '';
        var n = e.params.limit;
        out += 'نباید کوتاهتر از ' + (n) + ' کاراکتر باشد';
        break;
      case 'minProperties':
        out = '';
        var n = e.params.limit;
        out += 'نباید کمتر از ' + (n) + ' مشخصه داشته باشد';
        break;
      case 'multipleOf':
        out = 'باید شامل تعدادی ' + (e.params.multipleOf) + ' باشد';
        break;
      case 'not':
        out = 'با توجه به اسکیمای "not" ،نباید معتبر باشد';
        break;
      case 'oneOf':
        out = 'باید دقیقا با یک آیتم در اسکیمای "oneOf" تطابق داشته باشد';
        break;
      case 'pattern':
        out = 'باید با پترن "' + (e.params.pattern) + '" تطابق داشته باشد';
        break;
      case 'patternRequired':
        out = 'باید دارای پترن تطبیق مشخصه "' + (e.params.missingPattern) + '" باشد';
        break;
      case 'propertyNames':
        out = 'نام مشخصه \'' + (e.params.propertyName) + '\' نامعتبر است';
        break;
      case 'required':
        out = 'باید مشخصه اجباری ' + (e.params.missingProperty) + ' را دارا باشد';
        break;
      case 'switch':
        out = 'باید اعتبارسنجی کلمه کلیدی "switch" را زمانیکه ' + (e.params.caseIndex) + ' دارای خطاست، پاس کند';
        break;
      case 'type':
        out = 'باید برابر ' + (e.params.type) + ' باشد';
        break;
      case 'uniqueItems':
        out = 'نباید آیتم های تکراری داشته باشد (items ## ' + (e.params.j) + ' و ' + (e.params.i) + ' یکسان هستند)';
        break;
      default:
        continue;
    }
    e.message = out;
  }
};
