<template>
    <b-card :title="$t('title')">
        <b-card-text>
            <label>{{$t('cookie-name')}}</label>
            <b-form-input type="text" v-model="o.cookieName" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('header-name')}}</label>
            <b-form-input type="text" v-model="o.headerName" readonly dir="ltr"></b-form-input>
        </b-card-text>
    </b-card>
</template>
<script>
import loc from './Antiforgery.i18n.json';
import { i18n } from "../../utility/VueI18n";
export default {
    i18n: i18n(loc),
    data() {
        return {
            o: {
                cookieName: '',
                headerName: ''
            }
        };
    },
    async mounted() {
        try {
            var data = await this.$api.get('/api/antiforgery/AntiforgeryOptions/ViewOptions', null);
            this.o = Object.assign({}, this.o, data);
        } catch (ex) {
            console.error(ex);
        }
    }
}
</script>