<template>
    <b-card :title="$t('title')">
        <b-card-text>
            <label>{{$t('appUniqueId')}}</label>
            <b-form-input type="text" v-model="o.appUniqueId" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('directoryPath')}}</label>
            <b-form-input type="text" v-model="o.directoryPath" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('redisHost')}}</label>
            <b-form-input type="text" v-model="o.redisHost" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('redisPort')}}</label>
            <b-form-input type="number" v-model="o.redisPort" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('redisKey')}}</label>
            <b-form-input type="text" v-model="o.redisKey" readonly dir="ltr"></b-form-input>
        </b-card-text>
    </b-card>
</template>

<script>
import loc from './DataProtection.i18n.json';
import { i18n } from "../../utility/VueI18n";
export default {
    i18n: i18n(loc),
    data() {
        return {
            o: {
                appUniqueId: '',
                directoryPath: '',
                redisHost: '',
                redisPort: 0,
                redisKey: '',
            }
        };
    },
    async mounted() {
        try {
            var data = await this.$api.get('/api/dataprotection/DataProtectionOptions/ViewOptions');
            this.o = Object.assign({}, this.o, data);
        } catch (ex) {
            console.error(ex);
        }
    },
    methods: {
        isFileSystem() {
            return this.o.redisHost === undefined || this.o.redisHost === null || this.o.redisHost.length === 0;
        }
    }
}
</script>