<template>
    <b-card :title="$t('title')">
        <b-card-text>
            <label>{{$t('extensions')}}</label>
            <b-form-input type="text" v-model="o.thumberOptions.extensions" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('tempPath')}}</label>
            <b-form-input type="text" v-model="o.thumberOptions.tempPath" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('widthParameterName')}}</label>
            <b-form-input type="text" v-model="o.thumberOptions.widthParameterName" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('heightParameterName')}}</label>
            <b-form-input type="text" v-model="o.thumberOptions.heightParameterName" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('qualityParameterName')}}</label>
            <b-form-input type="text" v-model="o.thumberOptions.qualityParameterName" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('watermarkParameterName')}}</label>
            <b-form-input type="text" v-model="o.thumberOptions.watermarkParameterName" readonly dir="ltr"></b-form-input>
        </b-card-text>
    </b-card>
</template>
<script>
import loc from './ImageCompression.i18n.json';
import { i18n } from "../../utility/VueI18n";
export default {
    i18n: i18n(loc),
    data() {
        return {
            o: {
                thumberOptions: {
                    extensions: '',
                    tempPath: '',
                    widthParameterName: '',
                    heightParameterName: '',
                    qualityParameterName: '',
                    watermarkParameterName: '',
                }
            }
        };
    },
    async mounted() {
        try {
            var data = await this.$api.get('/api/imagecompression/ImageCompressionOptions/ViewOptions', null);
            this.o = Object.assign({}, this.o, data);
        } catch (ex) {
            console.error(ex);
        }
    }
}
</script>