<template>
    <b-card :title="$t('title')">
        <b-card-text>
            <label>{{$t('headerName')}}</label>
            <b-form-input type="text" v-model="o.headerName" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('defaultCulture')}}</label>
            <b-form-input type="text" v-model="o.defaultCulture" readonly dir="ltr"></b-form-input>
            <label class="mt-4">{{$t('activeCultures')}}</label>
            <br />
            <div v-for="c in o.activeCultures" :key="c" class="mt-2" :style="{color: 'red'}">{{getName(c)}}</div>
        </b-card-text>
    </b-card>
</template>
<script>
import loc from './Localization.i18n.json';
import { i18n } from "../../utility/VueI18n";
import { ServerValues } from "../../utility/ServerValues";
export default {
    i18n: i18n(loc),
    data() {
        return {
            o: {
                headerName: '',
                defaultCulture: '',
                activeCultures: [],
                languages: []
            }
        };
    },
    async mounted() {
        try {
            var data = await this.$api.get('/api/localization/LocalizationOptions/ViewOptions', null);
            this.o = Object.assign({}, this.o, data);
        } catch (ex) {
            console.error(ex);
        }
    },
    methods: {
        getName(culture) {
            var c = this.o.languages.find(a => a.culture === culture);
            return c.name;
        }
    }
}
</script>