<template>
    <b-alert show :variant="variant">
        <w-verify-code
            v-model="verifying"
            type="email"
            :email="email"
            :request-url="urlParser.localizeUrl('/api/usermanager/Profile/RequestCodeForConfirmingEmail').toRelative()"
            :verify-url="urlParser.localizeUrl('/api/usermanager/Profile/VerifyCodeForConfirmingEmail').toRelative()"
            @done="verified"
        ></w-verify-code>
        {{message}}
        <div v-show="!value">
            <b-form-group
                :label="$t('password')"
                label-for="profile-password-for-confirm-email"
                :state="passwordState"
                :invalid-feedback="invalidPassword"
                v-show="readyToConfirm"
            >
                <b-form-input
                    type="password"
                    id="profile-password-for-confirm-email"
                    v-model="password"
                    :disabled="confirming"
                ></b-form-input>
            </b-form-group>
            <b-button
                variant="primary"
                :disabled="verifying"
                @click="verify"
                v-show="!readyToConfirm"
            >{{$t('confirm-email')}}</b-button>
            <b-button varint="success" :disabled="confirming" @click="confirm" v-show="readyToConfirm">{{$t('save')}}</b-button>
        </div>
    </b-alert>
</template>

<script>
import { UrlParser } from "../../utility/url";
export default {
    props: {
        value: {
            default: false
        },
        email: {
            default: ''
        }
    },
    data() {
        return {
            verifying: false,
            confirming: false,
            readyToConfirm: false,
            password: '',
            urlParser: new UrlParser()
        };
    },
    methods: {
        verify() {
            this.verifying = true;
        },
        verified() {
            this.readyToConfirm = true;
        },
        async confirm() {
            try {
                this.confirming = true;
                await this.$api.post('/api/usermanager/Profile/ConfirmEmail', {
                    password: this.password,
                    email: this.email
                });
                this.toastSuccess();
                // reset
                this.password = '';
                this.verifying = false;
                this.readyToConfirm = false;
                // change value
                this.$emit('input', true);
            } catch (error) {
                this.toastError(error);
                console.log(error);
            } finally {
                this.confirming = false;
            }
        }
    },
    computed: {
        message() {
            return this.value ? this.$t('email-confirmed') : this.$t('email-not-confirmed');
        },
        variant() {
            return this.value ? 'success' : 'warning';
        },
        passwordState() {
            return this.password !== undefined && this.password !== null && this.password.length > 0;
        },
        invalidPassword() {
            if (this.passwordState) {
                return '';
            }
            return this.$t('password-required');
        },
    }
}
</script>