import Vue from 'vue';
import Vuex from 'vuex';
import api from './api';
import shorten from './shorten';
import user from './user';
import { responsiveMixin } from "./responsive/responsiveMixin";
import { responsiveStoreModule } from "./responsive/responsiveStoreModule";
import toastMixin from './toasting/toastMixin';

Vue.use(api);
Vue.use(shorten);
Vue.use(user);

Vue.mixin(responsiveMixin());
Vue.prototype.$store = new Vuex.Store({
    modules: {
        bp: responsiveStoreModule
    }
});

Vue.mixin(toastMixin);