export function shorten(str, length) {
    if (str === undefined || str === null) {
        return '';
    }
    if (str.length < length) {
        return str;
    }
    return str.substr(0, length) + ' ...';
}
export default {
    shorten,
    install(Vue, options) {
        Vue.prototype.$shorten = shorten;
    }
}