export class QueryMedia {
    /**
     * Callback for query device width
     * @callback queryMediaCallback
     * @param {Boolean} isMatch
     */
    /**
     * Checks for device resize and calls the given callback async
     * @param {String} query
     * @param {Boolean} callOnChange
     * @param {queryMediaCallback} callback
     */
    run(query, callOnChange, callback) {
        var x = window.matchMedia(query);
        var call = function () {
            callback(x.matches);
        };
        if (callOnChange) {
            x.addListener(call);
        }
        call();
    }
    /**
     * Checks device width sync
     * @param {String} query device width query. e.g: '(max-width: 769px)'
     * @returns {Boolean}
     */
    runSync(query = '(max-width: 769px)') {
        var res = false;
        this.run(query, false, isMatch => {
            res = isMatch;
        });
        return res;
    }
}