export class ValidCultures {
    constructor() {
        this.list = {
            fa: "فارسی",
            en: "English",
            ar: "العربیه",
            de: "Deutsche",
            es: "Español",
            fr: "Français",
            ps: "پښتو",
            ru: "русский",
            tr: "Türk",
            ur: "اردو",
        };
    }
    /**
     * Gets all cultures as object of { <culture_code>, <natve_name> }
     * @returns {Object} : an object like this { fa: 'فارسی', en: 'English', .... }
     */
    all() {
        return this.list;
    }
}