import VueI18n from 'vue-i18n';
import { ServerValues } from "./ServerValues";


/**
 * Add vue-i18n localization
 * @param {Object} messages 
 */
export function i18n(messages) {
    const serverValues = new ServerValues().getValues();
    return new VueI18n({
        locale: serverValues['culture'],
        fallbackLocale: serverValues['fallback-culture'],
        messages: messages
    });
};