import { objectToFormData } from "./util";

export class FormDataExtensions {
    /**
     * put anything inside a FormData object
     * @param {FormData} formDataObject 
     * @param {any} value 
     * @returns {void}
     */
    put(formDataObject, value) {
        return objectToFormData(value, {
            indices: true,
            nulls: true
        }, formDataObject, '');
    }
    /**
     * Prints the given form data object in console
     * @param {FormData} formDataObject 
     * @returns {void}
     */
    print(formDataObject) {
        if (formDataObject.entries === undefined || formDataObject.entries === null) {
            console.log('formdata.entries not supported in this browser: ' + navigator.userAgent);
            return;
        }
        for (var pair of formDataObject.entries()) {
            console.log(pair[0] + ', ' + pair[1]);
        }
    }
}