/**
 * gets css for opacity between 0 and 1
 * @param {Number} opacity value between 0 and 1
 * @returns {String}
 */
export function getOpacityCss(opacity) {
    var o1 = opacity;
    var o2 = 100 * o1;
    var msOpacity = `;-ms-filter: "progid:DXImageTransform.Microsoft.Alpha(Opacity=${o2})"`;
    var ie7Opacity = `filter: alpha(opacity=${o2})`;
    var ie7Opacity = `filter: alpha(opacity=${o2})`;
    var netscapeOpacity = `-moz-opacity: ${o1}`;
    var safariOpacity = `-khtml-opacity: ${o1}`;
    var chromeOpacity = `opacity: ${o1}`;
    return `${msOpacity};${ie7Opacity};${netscapeOpacity};${safariOpacity};${chromeOpacity}`;
}