/**
 * 
 * @param {String} jsonSchema 
 * @returns {String}
 */
export function normalizeJsonSchema(jsonSchema) {
    var s;
    if (jsonSchema === undefined || jsonSchema === null || jsonSchema.length === 0) {
        s = '{}';
    } else {
        s = jsonSchema;
    }
    var k = JSON.parse(s);
    if (k['$schema'] !== undefined) {
        delete k['$schema'];
    }
    return JSON.stringify(k);
}