export function removeMany(array, indices) {
    if (indices === null || indices === undefined) {
        return array;
    }
    var k = _.sortBy(indices, function (x) { return x; });
    for (var i = k.length - 1; i >= 0; i--) {
        var index = k[i];
        if (index === null || index === undefined) {
            continue;
        }
        array.splice(index, 1);
    }
    return array;
}